Polygon CreatePolygon()
{
    Polygon polygon = new Polygon();
    polygon.Points.AddRange(new Point2D[] {
        new Point2D(0,0),
        new Point2D(100, 50),
        new Point2D(200,0),
        new Point2D(300,50)
    });
    polygon.Stroke = new Stroke(Color.Black, 2, DashStyle.Dash);
    polygon.Fill = new SolidFill(Color.Blue);
    return polygon;
}

